import React, {useState} from 'react'
import {Grid, Segment, Header, Button} from 'semantic-ui-react'

import pokemon from './pokemonDatabase.json'

import * as R from 'ramda'


function App() {
  // You have to use react state in a component
  // team: array of pokedex_number
  const [team, setTeam] = useState([])
  return (
    <Grid>
      <Grid.Row>
        <Grid.Column width={10}>
          <Segment basic>
          {
            R.map(
              ({name, pokedex_number}) => (
                <Segment key={pokedex_number} basic>
                  <Segment attached>
                    <Header content={`${pokedex_number} ${name}`} />
                  </Segment>
                  <Button.Group attached="bottom">
                    <Button>Info</Button>
                    <Button content="Add to team"
                      onClick={
                        (event, data) => {
                          console.log(event, data)
                          setTeam(R.append(name, team))
                        }
                      }

                    />
                  </Button.Group>
                </Segment>
              ),
              pokemon
            )
          }
          </Segment>
        </Grid.Column>
        <Grid.Column width={6}>
          {JSON.stringify(team)}
        </Grid.Column>
      </Grid.Row>
    </Grid>
  )
}

export default App;
